
public class Calculadora {

	public int somar(int valor, int outroValor ){
		return valor + outroValor;
	}
	
	public int subtracao(int valor, int outroValor){
		return valor - outroValor;
	}

	public int multiplicar(int valor, int outroValor) {
		// TODO Auto-generated method stub
		return valor * outroValor;
	}
	
}
